'use strict';

var Injector = require('injector');
var _ = require('lodash');
var aliOss = require('ali-oss');

module.exports = Injector.define([
    'config/ali_oss'
], createModule);

function createModule(aliOssConfig) {
    return _.mapValues(aliOssConfig, function (config) {
        return aliOss(config);
    });
}