'use strict';
/* jshint mocha: true */

var path = require('path');
var expect = require('chai').expect;
var Injector = require('injector');
var _ = require('lodash');

var projectPath = path.resolve(__dirname, '..');
var includePaths = [projectPath + '/lib', projectPath + '/example'];

describe('test/test_funcs', function () {
    it('#it should have correct functions and variable', function () {
        function test(mongodbFixture) {
            // jshint -W030
            expect(_.size(mongodbFixture)).to.equal(2);
            // jshint +W030
        }
        var injector = new Injector(includePaths, {});
        var moduleDefinition = Injector.define([
            'component/mongodb'
        ], test);
        injector.inject(moduleDefinition);
    });
});