'use strict';
/* jshint mocha: true */
var path = require('path');
var expect = require('chai').expect;
var Injector = require('injector');
var winston = require('winston');

var projectPath = path.resolve(__dirname, '..');
var includePaths = [projectPath + '/lib', projectPath + '/example'];

describe('test-instance', function () {
    it('#It should return winston logger instances', function () {
        function test(logger) {
            expect(logger.console).to.be.instanceOf(winston.Logger);
            expect(logger.file).to.be.instanceOf(winston.Logger);
            expect(logger.memory).to.be.instanceOf(winston.Logger);
            expect(logger.http).to.be.instanceOf(winston.Logger);
            expect(logger.rotateFile).to.be.instanceOf(winston.Logger);
        }
        var injector = new Injector(includePaths, {});
        var moduleDefinition = Injector.define([
            'component/logger'
        ], test);
        injector.inject(moduleDefinition);
    });
});